import React from 'react';

import { Redirect, Route } from 'react-router-dom';



const PrivateRoute = ({ component: Component, ...rest }) => {
  const isAuthenticated = !!localStorage.getItem('user'); // Kiểm tra người dùng đã đăng nhập chưa

  return (
    <Route
      {...rest}
      render={props =>
        isAuthenticated ? (
          <Component {...props} /> // Cho phép vào trang nếu đã đăng nhập
        ) : (
          <Redirect to="/login" /> // Chuyển hướng đến login nếu chưa đăng nhập
        )
      }
    />
  );
};


export default PrivateRoute;
