import React, { useState } from "react";
import { useHistory } from "react-router-dom";

import"../../pages/auth/login.css";
import"../../pages/auth/resets.scss";
import"../../pages/auth/style.css";
import HeroSlide from "../../components/hero-slide/HeroSlide";
import Header from "../../components/header/Header";
const Login = () => {
  const [username, setUser] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  const handleLogin = async (e) => {
    e.preventDefault(); // Ngăn chặn hành vi mặc định của form
    // Kiểm tra thông tin đăng nhập
    // Ở đây bạn có thể gọi API để xác thực tài khoản/mật khẩu
    // Ví dụ: const response = await api.login(user, password);
    try{ const response = await fetch('http://localhost:5000/api/auth/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ usernameOremail:username, password: password }),
    });
    const data = await response.json();
    if (response.ok) {
      // Lưu thông tin người dùng vào localStorage
      localStorage.setItem('user', JSON.stringify(data));
      history.push('/'); // Điều hướng về trang chủ sau khi đăng nhập thành công
    } else {
      alert("Đăng nhập không thành công. Vui lòng kiểm tra lại tài khoản và mật khẩu.");
    }}
    catch(err){
      console.log(err);
    }
  }
  // Hàm này sẽ được gọi khi người dùng nhấn nút "Login"
  // Bạn có thể thay thế bằng API thực tế của bạn để xác thực người dùng
  return (
    <div>
     <Header/>
    <div className="login-page">
   
        <div className="container signinForm">
      {/* Phần LOGIN */}
      <div className="form signin">
        <h2>Sign In</h2>
        <div className="inputbox">
          <input
            type="text"
            value={username}
            onChange={(e) => setUser(e.target.value)}
            required
          />
          <i className="uil uil-user use name"></i>
          <span>Username</span>
        </div>
        <div className="inputbox">
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <i className="uil uil-lock password"></i>
          <span>Password</span>
        </div>
        <div className="inputbox">
          <input
            type="submit"
            value="Login"
            onClick={handleLogin} // Gọi hàm handleLogin khi nhấn nút Login
          />
        </div>
        <p>
          Not Registered? <a href="/register" className="create">Create an account</a>
        </p>
      </div>
    </div>
    </div>
    </div>
  );
};

export default Login;
