import { BrowserRouter, Route, Switch, useLocation } from 'react-router-dom';
import Header from './components/header/Header';
import Footer from './components/footer/Footer';
import Routes from './routes/Routes.jsx';
import Login from './pages/auth/login.jsx'; // import thêm trang Login
import Register from './pages/auth/register.jsx'; // import thêm trang Register
import './App.scss';
import 'swiper/swiper.min.css';
import './assets/boxicons-2.0.7/css/boxicons.min.css';
import PrivateRoute from './routes/PrivateRoute.jsx'; // import thêm PrivateRoute


function Layout() {
    const location = useLocation();
    const hideHeaderFooter = location.pathname === '/login' || location.pathname === '/register';

    return (
        <>
            {!hideHeaderFooter && <Header />}
            <Routes />
            {!hideHeaderFooter && <Footer />}
        </>
    );
}

function App() {
    return (
        <BrowserRouter>
            <Switch>
               
            </Switch>
        </BrowserRouter>
    );
}

export default App;
