import React, { useState } from "react";
import { useHistory } from "react-router-dom";

import "../../pages/auth/login.css";
import "../../pages/auth/resets.scss";
import "../../pages/auth/style.css";
import HeroSlide from "../../components/hero-slide/HeroSlide";
import Header from "../../components/header/Header";

const Login = () => {
  const [username, setUser] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ usernameOremail: username, password }),
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem('user', JSON.stringify(data));
        history.push('/');
      } else {
        alert("Đăng nhập không thành công. Vui lòng kiểm tra lại tài khoản và mật khẩu.");
      }
    } catch (err) {
      console.log(err);
      alert("Đã có lỗi xảy ra. Vui lòng thử lại.");
    }
  };

  return (
    <div className="login-page" style={{ backgroundColor: "#0d0d0d", minHeight: "100vh", paddingTop: "60px" }}>
      <Header />
      <div className="container" style={{ display: "flex", justifyContent: "center", alignItems: "center" }}>
        <div className="form signin" style={{
          backgroundColor: "#1a1a1a",
          padding: "40px",
          borderRadius: "12px",
          boxShadow: "0 0 20px rgba(255, 0, 0, 0.3)",
          width: "100%",
          maxWidth: "400px",
        }}>
          <h2 style={{ color: "#fff", textAlign: "center", marginBottom: "20px" }}>Sign In</h2>
          <form onSubmit={handleLogin}>
            <div className="inputbox" style={{ marginBottom: "20px" }}>
              <input
                type="text"
                value={username}
                onChange={(e) => setUser(e.target.value)}
                placeholder="Username or Email"
                style={inputStyle}
                required
              />
            </div>
            <div className="inputbox" style={{ marginBottom: "20px" }}>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Password"
                style={inputStyle}
                required
              />
            </div>
            <div className="inputbox" style={{ marginBottom: "20px" }}>
              <input
                type="submit"
                value="Login"
                style={{
                  ...inputStyle,
                  backgroundColor: "#e50914",
                  color: "#fff",
                  fontWeight: "bold",
                  cursor: "pointer",
                  border: "none",
                }}
              />
            </div>
          </form>
          <p style={{ color: "#ccc", textAlign: "center" }}>
            Don't have an account?{" "}
            <a href="/register" style={{ color: "#e50914", textDecoration: "underline" }}>
              Create one
            </a>
          </p>
        </div>
      </div>
    </div>
  );
};

const inputStyle = {
  width: "100%",
  padding: "12px 16px",
  borderRadius: "6px",
  backgroundColor: "#333",
  border: "1px solid #555",
  color: "#fff",
  outline: "none",
  fontSize: "16px",
};

export default Login;
