import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import Header from "../../components/auth/AuthHeader.jsx";

const Register = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const history = useHistory();

  const handleRegister = async (e) => {
    e.preventDefault(); // Sửa lỗi chính tả ở đây

    // Kiểm tra thông tin đăng ký
    if (!username || !email || !password || !confirmPassword) {
      alert("Vui lòng điền đầy đủ thông tin.");
      return;
    }

    if (password !== confirmPassword) {
      alert("Mật khẩu xác nhận không khớp.");
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, email, password }), // Đảm bảo gửi đúng các trường
      });

      const data = await response.json();

      if (response.ok) {
        // Lưu thông tin người dùng vào localStorage
        localStorage.setItem("user", JSON.stringify(data)); // Lưu dữ liệu vào localStorage
        history.push("/login"); // Điều hướng về trang đăng nhập sau khi đăng ký thành công
      } else {
        alert("Đăng ký không thành công. Vui lòng kiểm tra lại thông tin.");
      }
    } catch (err) {
      console.log(err);
      alert("Có lỗi xảy ra khi đăng ký. Vui lòng thử lại.");
    }
  };

  return (
    <div className="register-page">
      <Header />
      <div className="container">
        <div className="form signup">
          <h2>Sign Up</h2>
          <form onSubmit={handleRegister}>
            <div className="inputbox">
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
              <i className="uil uil-user"></i>
              <span>Username</span>
            </div>
            <div className="inputbox">
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <i className="uil uil-envelope-alt"></i>
              <span>Email Address</span>
            </div>
            <div className="inputbox">
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <i className="uil uil-lock"></i>
              <span>Create Password</span>
            </div>
            <div className="inputbox">
              <input
                type="password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
              <i className="uil uil-lock"></i>
              <span>Confirm Password</span>
            </div>
            <div className="inputbox">
              <input
                type="submit"
                value="Create Account"
              />
            </div>
            <p>
              Already a member? <a href="/login" className="login">Log in</a>
            </p>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Register;
